mapper.insert:
INSERT INTO book_isbn_temp (isbn13, book_title, publisher, infonaru_fetched_at, status_cd) VALUES
(#{bookIsbnTemp.isbn13}, #{bookIsbnTemp.bookTitle}, #{bookIsbnTemp.publisher}, #{bookIsbnTemp.infonaruFetchedAt}, #{bookIsbnTemp.statusCd})
ON DUPLICATE KEY UPDATE book_title = COALESCE(VALUES(book_title), book_title), publisher = COALESCE(VALUES(publisher), publisher), infonaru_fetched_at = VALUES(infonaru_fetched_at);

mapper.update:
UPDATE BOOK_ISBN_TEMP
SET
NAVER_FETCHED_AT = #{naverFetchedAt},
NAVER_RES_STATUS = #{naverResStatus}
WHERE ISBN13 = #{isbn13}

UPDATE BOOK_ISBN_TEMP
SET
ALADIN_FETCHED_AT = #{aladinFetchedAt},
ALADIN_RES_STATUS = #{aladinResStatus}
WHERE ISBN13 = #{isbn13}

UPDATE BOOK_ISBN_TEMP
SET DATA_HASH = #{dataHash}
WHERE ISBN13 = #{isbn13}
