mapper.select:
SELECT COALESCE(p.GENRE_ID, c.GENRE_ID) AS genreId,
CASE WHEN COALESCE(p.GENRE_NM, c.GENRE_NM) LIKE '\\_%' ESCAPE '\\' THEN SUBSTRING(COALESCE(p.GENRE_NM, c.GENRE_NM), 2) ELSE COALESCE(p.GENRE_NM, c.GENRE_NM) END AS genreNm,
NULL AS parentId FROM BOOK_ISBN b JOIN GENRE c ON c.GENRE_ID = b.GENRE_ID LEFT JOIN GENRE p ON p.GENRE_ID = c.PARENT_ID WHERE c.MALL = '국내도서' GROUP BY COALESCE(p.GENRE_ID, c.GENRE_ID), CASE WHEN COALESCE(p.GENRE_NM, c.GENRE_NM) LIKE '\\_%' ESCAPE '\\' THEN SUBSTRING(COALESCE(p.GENRE_NM, c.GENRE_NM), 2) ELSE COALESCE(p.GENRE_NM, c.GENRE_NM) END HAVING COUNT(*) >= #{min} ORDER BY COUNT(*) DESC LIMIT #{top}

SELECT c.GENRE_ID AS genreId,
CASE WHEN c.GENRE_NM LIKE '\\_%' ESCAPE '\\' THEN SUBSTRING(c.GENRE_NM, 2) ELSE c.GENRE_NM END AS genreNm,
c.PARENT_ID AS parentId FROM BOOK_ISBN b JOIN GENRE c ON c.GENRE_ID = b.GENRE_ID WHERE c.MALL = '국내도서' AND c.PARENT_ID = #{parentId} GROUP BY c.GENRE_ID, CASE WHEN c.GENRE_NM LIKE '\\_%' ESCAPE '\\' THEN SUBSTRING(c.GENRE_NM, 2) ELSE c.GENRE_NM END, c.PARENT_ID HAVING COUNT(*) >= #{min} ORDER BY COUNT(*) DESC LIMIT #{top}

SELECT COALESCE(p.GENRE_ID, c.GENRE_ID) AS genreId,
CASE WHEN COALESCE(p.GENRE_NM, c.GENRE_NM) LIKE '\\_%' ESCAPE '\\' THEN SUBSTRING(COALESCE(p.GENRE_NM, c.GENRE_NM), 2) ELSE COALESCE(p.GENRE_NM, c.GENRE_NM) END AS genreNm,
NULL AS parentId, '외국도서' AS mall FROM BOOK_ISBN b JOIN GENRE c ON c.GENRE_ID = b.GENRE_ID LEFT JOIN GENRE p ON p.GENRE_ID = c.PARENT_ID WHERE c.MALL = '외국도서' GROUP BY COALESCE(p.GENRE_ID, c.GENRE_ID), CASE WHEN COALESCE(p.GENRE_NM, c.GENRE_NM) LIKE '\\_%' ESCAPE '\\' THEN SUBSTRING(COALESCE(p.GENRE_NM, c.GENRE_NM), 2) ELSE COALESCE(p.GENRE_NM, c.GENRE_NM) END HAVING COUNT(*) >= #{min} ORDER BY COUNT(*) DESC LIMIT #{top}
