mapper.select:
SELECT BOOKMARK_YN
FROM BOOKMARK
WHERE USER_ID = #{userId}
  AND ISBN13 = #{isbn13}

mapper.update:
UPDATE BOOKMARK
SET BOOKMARK_YN = #{bookmarkYn},
    UPDATED_AT = NOW()
WHERE USER_ID = #{userId}
  AND ISBN13 = #{isbn13}

mapper.delete:
DELETE FROM RESERVATIONS
WHERE USER_ID = #{userId}
  AND RSV_ID  = #{rsvId}
